and std.TYPE_CODE in
#{item}
and (exists (select cate_code from emr_grade_dept where pk_dept = #{pkDept,jdbcType=VARCHAR} and cate_code = std.cate_code) or (not exists (select cate_code from emr_grade_dept where pk_dept = #{pkDept,jdbcType=VARCHAR}) and nvl(std.cate_code, '00') = '00') )
and std.cate_code=#{cateCode,jdbcType=VARCHAR}
order by std.type_code,std.code,std.name

