select rec.PK_REC, rec.PK_PATREC, rec.NAME, rec.SEQ_NO, rec.REC_DATE, rec.DESCRIBE, rec.PK_PI, rec.TIMES, rec.PK_PV, rec.PK_DEPT, rec.PK_WARD, rec.TYPE_CODE, rec.PK_TMP, rec.PK_DOC, rec.FLAG_AUDIT, rec.EU_AUDIT_LEVEL, rec.EU_DOC_STATUS, rec.EU_AUDIT_STATUS, rec.PK_EMP_REFER, rec.REFER_SIGN_DATE, rec.PK_EMP_CONSULT_ACT, rec.PK_EMP_CONSULT, rec.CONSULT_AUDIT_DATE, rec.CONSULT_SIGN_DATE, rec.PK_EMP_DIRECTOR_ACT, rec.PK_EMP_DIRECTOR, rec.DIRECTOR_AUDIT_DATE, rec.DIRECTOR_SIGN_DATE, rec.SUBMIT_DATE, rec.DEL_FLAG, rec.REMARK, rec.CREATOR, rec.CREATE_TIME, doc.doc_data, doc.doc_xml, tmp.PK_TMP, tmp.PK_ORG, tmp.CODE, tmp.NAME, tmp.DESCRIBE, tmp.TYPE_CODE, tmp.EU_TMP_LEVEL, tmp.TMP_TITLE, tmp.TEXT_TITLE, tmp.SORT_CODE, tmp.PY_CODE, tmp.D_CODE, tmp.PK_DEPT, tmp.PK_WARD, tmp.EU_STATUS, tmp.FLAG_AUDIT, tmp.AUDIT_LEVEL, tmp.LIMIT_NUM, tmp.FLAG_DEFAULT, tmp.FLAG_OPEN, tmp.FLAG_SYS, tmp.EU_TYPE, tmp.FLAG_EXT, tmp.PK_EMP, tmp.DEL_FLAG, tmp.TMP_PATH, tmp.REMARK, tmp.CREATOR, tmp.CREATE_TIME, tmp.TS, tmp.AUDIT_LEVEL_SET, typ.* from emr_med_rec rec inner join emr_med_doc doc on rec.pk_doc = doc.pk_doc left outer join emr_template tmp on rec.pk_tmp = tmp.pk_tmp left outer join emr_doc_type typ on tmp.type_code = typ.code where rec.pk_pv = #{pkPv,jdbcType=CHAR} and rec.del_flag !='1'
and rec.type_code like #{typeCode,jdbcType=VARCHAR}
and rec.create_time >= #{beginDate,jdbcType=DATE}
and rec.create_time < #{endDate,jdbcType=DATE}
and rec.name like '%'||#{titleName,jdbcType=VARCHAR}||'%'
${orderBy}

