and eu_msg_type = #{euMsgType,jdbcType=VARCHAR}
${orderBy}

select item.*, rec.score grade_score,rec.qc_date, std.code std_code,std.name std_name,std.score_stand,std.score_type,std.single_score,std.flag_time, typ.code type_code,typ.name type_name,typ.score type_score, CASE rec.eu_grade WHEN 'A' THEN '甲级' WHEN 'B' THEN '乙级' WHEN 'C' THEN '丙级' ELSE '' END AS grade_name, emp.name as grade_emp_name from emr_grade_item item,emr_grade_rec rec,emr_grade_standard std,emr_grade_type typ,view_emr_emp_list emp where rec.pk_pv = #{pkPv,jdbcType=VARCHAR} and rec.pk_graderec=item.pk_graderec and rec.del_flag!='1' and item.del_flag!='1' and item.stand_code=std.code and std.type_code=typ.code and rec.pk_emp_qc=emp.pk_emp
and rec.eu_grade_type = #{euGradeType,jdbcType=VARCHAR}
and nvl(std.flag_time,'0') = #{flagTime,jdbcType=VARCHAR}
and ((nvl(std.flag_time,'0') != #{flagTimeNot,jdbcType=VARCHAR}) or (nvl(std.flag_time,'0') = '1' and nvl(item.pk_rec,'') = ''))
and ((item.flag_finish=#{flagFinish,jdbcType=VARCHAR} and item.EU_SCORE = '1') or item.EU_SCORE='0')
${orderBy}

select rec.pk_amend, rec.pk_org, rec.pk_pv, rec.pk_gradeitem, rec.pk_rec, rec.eu_type, rec.content, rec.pk_emp, rec.date_receive, rec.date_finish, rec.eu_status rec_eu_status, rec.pk_emp_create, rec.date_create, rec.remark, rec.del_flag, rec.creator, rec.create_time, rec.ts, rec.note_back, emp.name qc_emp_name, empdoc.name emp_name, std.type_code std_type_code, std.doc_type_code, std.code std_code, std.name std_name, std.score_stand, std.score_type, std.single_score, std.flag_time, typ.name type_name, typ.flag_course, typ.flag_home_page, nvl(mrec.name,typ.name) doc_name, pat.* from emr_amend_rec rec inner join view_emr_pat_list pat on rec.pk_pv=pat.pk_pv inner join emr_grade_item gitem on rec.pk_gradeitem=gitem.pk_gradeitem inner join emr_grade_rec grec on grec.pk_graderec=gitem.pk_graderec inner join emr_grade_standard std on gitem.stand_code=std.code and std.del_flag='0' inner join emr_doc_type typ on typ.code=std.doc_type_code left outer join emr_med_rec mrec on mrec.pk_rec=rec.pk_rec inner join view_emr_emp_list emp on emp.pk_emp=rec.pk_emp_create left outer join view_emr_emp_list empdoc on empdoc.pk_emp=rec.pk_emp where rec.del_flag != '1' and grec.del_flag != '1'
and rec.pk_pv = #{pkPv,jdbcType=CHAR}
and rec.pk_emp = #{pkEmp,jdbcType=VARCHAR}
and grec.pk_graderec = #{pkGraderec,jdbcType=VARCHAR}
and grec.eu_grade_type = #{euGradeType,jdbcType=VARCHAR}
and rec.eu_status != #{euStatusAmendNot,jdbcType=CHAR}
and (rec.eu_status in ('0','1') or ( rec.eu_status='2' and rec.date_create >= to_date(#{beginDate,jdbcType=VARCHAR},'yyyymmddhh24miss') and rec.date_create < to_date(#{endDate,jdbcType=VARCHAR},'yyyymmddhh24miss') ) )
order by rec.create_time

SELECT
    *
FROM
      emr_audit_rec
where pk_pv = #{pkPv,jdbcType=VARCHAR} and del_flag !='1' 

    and pk_rec = #{pkRec,jdbcType=VARCHAR}


    and eu_level = #{euLevel,jdbcType=VARCHAR}


    and eu_level <= #{euLevelLess,jdbcType=VARCHAR}
    order by eu_level desc


    and eu_level >= #{euLevelGreater,jdbcType=VARCHAR}
    order by eu_level


    and eu_level >= #{euLevelGreaterDesc,jdbcType=VARCHAR}
    order by eu_level desc

select pat.name, pat.bed_no, pat.code_ip, pat.pat_no, pat.pk_pi, pat.ip_times times, pat.age_txt, pat.birth_date, pat.dt_sex, pat.sex_name, rec.pk_pv, rec.eu_msg_type, item.*, gt.name as type_name, gs.name as stand_name, gs.flag_time, dept.name as dept_name, emp_qc.name as emp_qc_name, emp_send.name as emp_send_name from view_emr_pat_list pat inner join emr_grade_msg_rec rec on rec.pk_pv=pat.pk_pv inner join emr_grade_msg_item item on item.pk_grade_msgrec=rec.pk_grade_msgrec left outer join emr_grade_type gt on item.type_code=gt.code left outer join emr_grade_standard gs on item.stand_code=gs.code left outer join view_emr_dept_list dept on item.pk_dept = dept.pk_dept left outer join view_emr_emp_list emp_qc on emp_qc.pk_emp=item.pk_emp_qc left outer join view_emr_emp_list emp_send on emp_send.pk_emp=item.pk_emp_send where nvl(rec.del_flag,'0')='0' and nvl(item.del_flag,'0')='0'
and rec.pk_pv = #{pkPv,jdbcType=VARCHAR}
and rec.eu_msg_type = #{euMsgType,jdbcType=VARCHAR}
and item.pk_emp = #{pkEmp,jdbcType=VARCHAR}
and item.eu_status = #{euStatus,jdbcType=VARCHAR}
and rec.qc_date >= to_date(#{dateBegin,jdbcType=VARCHAR},'yyyyMMddhh24miss')
and rec.qc_date < to_date(#{dateEnd,jdbcType=VARCHAR},'yyyyMMddhh24miss')
order by rec.pk_pv,item.stand_code

select pv.NAME_PI name, pv.BED_NO, pv.name_emp_phy, pv.PK_PV, pi.CODE_IP, dept.PK_DEPT, dept.NAME_DEPT deptName, item.SCORE, item.eu_score, sta.NAME standardName, grade.pk_emp_qc from EMR_GRADE_REC grade inner join PV_ENCOUNTER pv on pv.PK_PV=grade.PK_PV inner join bd_ou_dept dept on pv.pk_dept=dept.pk_dept left outer join PI_MASTER pi on pi.PK_PI=pv.PK_PI left outer join emr_grade_item item on item.PK_GRADEREC=grade.PK_GRADEREC left outer join emr_grade_standard sta on item.STAND_CODE=sta.CODE where grade.DEL_FLAG='0'
and sta.cate_code =#{cateCode,jdbcType=VARCHAR}
and grade.EU_GRADE_TYPE =#{linkType,jdbcType=VARCHAR}
and grade.qc_date >= to_date(#{qcBegin,jdbcType=VARCHAR},'yyyy-mm-dd')
and grade.qc_date < to_date(#{qcEnd,jdbcType=VARCHAR},'yyyy-mm-dd')
and dept.pk_dept in
#{item}
and item.eu_score =#{euScore,jdbcType=VARCHAR}
and sta.name like '%'||#{linkDefect,jdbcType=VARCHAR}||'%'
order by pv.PK_PV,item.SCORE

select pv.pk_pi, pv.pk_pv, pv.code_pv, pi.name_pi name, pi.code_ip, pi.code_pi, pi.code_op, pi.dt_sex, zdsex.name name_sex, pi.birth_date, pv.age_pv, zdsex.name sex_name, pv.bed_no, pv.date_admit date_begin, pv.date_end, bd_hp.name hp_name, pvip.ip_times, pv.pk_dept, dept.name_dept dept_name, pv.pk_dept_ns, ns.name_dept dept_ns_name, patrec.diag_admit_txt, patrec.diag_dis_txt, patrec.pk_emp_refer, emp_refer.name_emp refer_name, patrec.pk_emp_consult, emp_consult.name_emp consult_name, patrec.pk_emp_director, emp_director.name_emp director_name, patrec.eu_status, patrec.pk_patrec from pv_encounter pv inner join pi_master pi on pv.pk_pi = pi.pk_pi inner join emr_pat_rec patrec on patrec.pk_pv = pv.pk_pv inner join pv_ip pvip on pv.pk_pv = pvip.pk_pv inner join bd_ou_dept dept on pv.pk_dept=dept.pk_dept left join bd_ou_dept ns on pv.pk_dept_ns=ns.pk_dept left outer join bd_hp on pv.pk_insu=bd_hp.pk_hp left outer join bd_defdoc zdsex on pi.dt_sex=zdsex.code and zdsex.code_defdoclist='000000' left outer join bd_ou_employee emp_refer on patrec.pk_emp_refer=emp_refer.pk_emp left outer join bd_ou_employee emp_consult on patrec.pk_emp_consult=emp_consult.pk_emp left outer join bd_ou_employee emp_director on patrec.pk_emp_director=emp_director.pk_emp where patrec.del_flag = '0'
and pv.PK_DEPT = #{pkDept,jdbcType=VARCHAR}
and patrec.pk_emp_refer = #{pkEmpRefer,jdbcType=VARCHAR}
and pi.name_pi like '%'||#{name,jdbcType=VARCHAR}||'%'
and pi.code_ip like '%'||#{codeIp,jdbcType=VARCHAR}||'%'
and nvl(patrec.flag_final_qc,'0') = #{flagFinalQc,jdbcType=CHAR}
and nvl(patrec.flag_emr_archive,'0') = #{flagEmrArchive,jdbcType=CHAR}
and pv.date_end >= to_date(#{beginDate,jdbcType=VARCHAR},'yyyymmddhh24miss')
and pv.date_end <= to_date(#{endDate,jdbcType=VARCHAR},'yyyymmddhh24miss')
order by pv.date_end, pi.name_pi,pv.pk_pv
