mapper.select:
SELECT PK_ELEMENT, PK_ORG, CODE, NAME, PY_CODE, D_CODE, EU_DATA_TYPE, EU_PRE_FORMAT, EU_CTRL_TYPE, WS_CODE, EU_CODE_TYPE, CODE_VALUE, EU_DICT_TYPE, DICT_CODE, SORT_NUM, DE_VALUE_LIST, edge_style, edge_style2, flag_edge, flag_del_protect, flag_edit_protect, help_tip, title, SHORT_NAME, flag_is_ctrl_hidden, PLACE_HOLDER, TEXT, flag_must_fill_content, flag_print_able, FLAG_IS_NOT_IN_XML, flag_view_secret, max_len, max_value, MIN_VALUE, preci, num_unit, NUM_ERR_INPUT_INFO, NUM_OUT_RANGE_INFO, check_box_status, check_pos, eu_date_format, DEFINITION, FONT_NAME, eu_font_size, eu_font_type, eu_align_type, Repulsion_For_Group, REMARK, IMG_INDEX, DEL_FLAG, CREATOR, CREATE_TIME, TS FROM EMR_DATA_ELEMENT where del_flag!='1'
and code = #{code,jdbcType=VARCHAR}
and name = #{name,jdbcType=VARCHAR}
and ${codeTypes}

SELECT
    *
FROM
     EMR_DICT_RANGE
WHERE            
    del_flag!='1'  

       and code = #{code,jdbcType=VARCHAR}


       and code != #{code_un,jdbcType=VARCHAR}


       and name = #{name,jdbcType=VARCHAR}


       and pk_range != #{pkRange,jdbcType=VARCHAR}

    order by sort_num,code

SELECT
    *
FROM
     EMR_DICT_RANGE_CODE
WHERE            
    del_flag!='1'  

       and code = #{code,jdbcType=VARCHAR}


       and name = #{name,jdbcType=VARCHAR}


       and pk_range_code != #{pkRangeCode,jdbcType=VARCHAR}


       and pk_range = #{pkRange,jdbcType=VARCHAR}

    order by sort_num,code

SELECT pk_sysparam pk_param,pk_org,code,name,null py_code,null d_code,val as value,null short_name,desc_param p_value,0 sort_num,note remark,'0' flag_show,del_flag,creator,create_time,ts FROM bd_sysparam where del_flag !='1' and code = #{code,jdbcType=VARCHAR}
and (pk_org like #{pkOrg,jdbcType=VARCHAR} or pk_org like '~%')
and exists (select 1 from bd_sysparam_temp where bd_sysparam_temp.dt_pacate ='07' and bd_sysparam.pk_paramtemp=bd_sysparam_temp.pk_paramtemp) order by pk_org

SELECT * FROM EMR_PARAMETER where del_flag!='1' and code = #{code,jdbcType=VARCHAR}

     and pk_org like #{pkOrg,jdbcType=VARCHAR} /*or pk_org like '~%'*/

 order by pk_org

SELECT * FROM EMR_SWITCH where del_flag!='1' 
and code = #{code,jdbcType=VARCHAR}

     and pk_org like #{pkOrg,jdbcType=VARCHAR} /* or pk_org like '~%'*/

mapper.update:
UPDATE EMR_SN_GENERATOR set value= nvl(value,0) +1
WHERE 
    CODE = #{code,jdbcType=VARCHAR}

mapper.select:
select bd.pk_defdoc pk_dictcode,bd.pk_org,bd.code_defdoclist pk_dictcls,bd.code,bd.name,bd.py_code,bd.d_code, bd.memo remark,bd.shortname as value,bd.creator,bd.create_time,bd.del_flag,bd.ts, bd.ba_code,bd.ba_code sort_num,bd.ba_code gb_code FROM bd_defdoc bd where bd.code_defdoclist='060517'
and bd.pk_org = #{pkOrg,jdbcType=VARCHAR}
order by bd.ba_code,cast(bd.code as int)

select cd.* from emr_dict_class cla ,emr_dict_code cd where
cd.pk_org = #{pkOrg,jdbcType=VARCHAR}
cla.pk_dictcls=cd.pk_dictcls and cla.code='KnowledgeBaseType' order by cd.sort_num

select * from emr_knowledge_base kb where 

kb.pk_org = #{pkOrg,jdbcType=VARCHAR}

kb.code = #{code,jdbcType=VARCHAR} and 
kb.type_code = #{typeCode,jdbcType=VARCHAR}

select doc.* from emr_doctor doc where doc.del_flag='0'
and doc.pk_dept = #{pkDept,jdbcType=CHAR}
and doc.pk_emp = #{pkEmp,jdbcType=CHAR}
and doc.pk_wg = #{pkWg,jdbcType=CHAR}
and doc.pk_dept in (select d.pk_dept from bd_ou_dept d where substr(d.code_dept,1,7) = (select substr(dept.code_dept,1,7) from bd_ou_dept dept where dept.pk_dept = #{pkDepts,jdbcType=CHAR} ) and d.del_flag='0')
order by doc.eu_audit_level desc

select * from emr_doctor d where (d.py_code like '%${pyCode}%' or d.d_code like '%${dCode}%' or d.name like '%${pyCode}%' or d.code like '%${pyCode}%') and d.del_flag='0'
and d.PK_ORG like #{pkOrg,jdbcType=VARCHAR}
order by d.code

        select *
		  from emr_knowledge_base
		where nvl(del_flag,'0')!='1' 

               and type_code = #{typeCode,jdbcType=VARCHAR}


               and code = #{code,jdbcType=VARCHAR}

		order by code,name
