mapper.select:
SELECT pv.pk_pi, pv.bed_no, pv.name_pi, pdapdt.pk_pdapdt, pdapdt.pk_pv, pdapdt.pk_pd, pdapdt.pk_unit, pdapdt.pack_size, occ.quan_occ as quan_pack, pdapdt.pack_size*occ.quan_occ as quan_min, pdap.pk_dept_ap, ord.eu_status_ord, ord.date_stop, ord.drip_speed, ord.sort_iv, ord.code_supply, ord.dosage, ord.pk_unit_dos, ord.code_freq, ord.pk_cnord, ord.eu_always, ord.name_ord, ord.spec, ord.ordsn, ord.ordsn_parent, occ.quan_occ, occ.pk_exocc, occ.date_plan, unit. NAME unit, freq. NAME freq, unit_dos. NAME unit_dos, ppd.pk_pivascate, CASE WHEN chk.pk_pdchk IS NULL THEN '-1' ELSE chk.eu_result_manu END flag_chk FROM ex_pd_apply_detail pdapdt INNER JOIN cn_order ord ON pdapdt.pk_cnord = ord.pk_cnord and ord.code_supply != '01' INNER JOIN pv_encounter pv ON pdapdt.pk_pv = pv.pk_pv INNER JOIN ex_order_occ occ ON pdapdt.pk_pdapdt = occ.pk_pdapdt INNER JOIN bd_unit unit ON pdapdt.pk_unit = unit.pk_unit AND unit. NAME != 'ç‰‡' INNER JOIN bd_term_freq freq ON ord.code_freq = freq.code INNER JOIN bd_unit unit_dos ON ord.pk_unit_dos = unit_dos.pk_unit LEFT OUTER JOIN ex_pd_check chk ON pdapdt.pk_cnord = chk.pk_cnord LEFT OUTER JOIN bd_pivas_pd ppd ON pdapdt.pk_pd = ppd.pk_pd AND pdapdt.pk_org = ppd.pk_org LEFT OUTER JOIN EX_PD_APPLY pdap ON pdapdt.pk_pdap = pdap.pk_pdap INNER JOIN BD_PD pd ON pd.pk_pd = pdapdt.pk_pd
AND pdapdt.flag_stop = '0' AND pdapdt.flag_de = '0' AND NOT EXISTS ( SELECT 1 FROM ex_pd_pivas pivas WHERE pivas.pk_exocc = occ.pk_exocc )
AND pdapdt.pk_pdap in
#{item}
AND pdapdt.eu_detype = #{euDetype,jdbcType=VARCHAR}
AND pv.code_pv = #{codePv,jdbcType=VARCHAR}
AND ${wheresql}
order by ord.sort_iv

SELECT PV.CODE_PV, pv.bed_no, pi.name_pi, pivas.pk_pdpivas, pivas.pk_pivascate, pivas.pk_cnord, pivas.ordsn, pivas.ordsn_parent, pivas.eu_always, pivas.name_ord, pivas.spec, unit. NAME unit, pivas.quan_pack, pivas.quan_min, freq. NAME freq, pivas.dosage, unit_dos. NAME unit_dos, pivas.drip_speed, pivas.time_occ, pivas.time_plan, pivas.code_batch, bat.pk_pivasbatch, pivas.barcode, ord.eu_status_ord, ord.date_stop, pivas.flag_prt, pivas.code_supply, pivas.pk_pdapdt, pivas.pk_dept_apply FROM pv_encounter pv INNER JOIN pi_master pi ON pv.pk_pi = pi.pk_pi INNER JOIN ex_pd_pivas pivas ON pv.pk_pv = pivas.pk_pv INNER JOIN bd_unit unit ON pivas.pk_unit = unit.pk_unit INNER JOIN bd_unit unit_dos ON pivas.pk_unit_dos = unit_dos.pk_unit INNER JOIN bd_term_freq freq ON pivas.code_freq = freq.code INNER JOIN cn_order ord ON pivas.pk_cnord = ord.pk_cnord LEFT OUTER JOIN bd_pivas_cate pcate ON pivas.pk_pivascate = pcate.pk_pivascate LEFT OUTER JOIN bd_pivas_batch bat ON bat.code = pivas.code_batch and bat.del_flag = '0' and bat.pk_dept = #{pkDeptAdmix,jdbcType=VARCHAR}
and pivas.flag_stop = '0'
AND pivas.pk_dept_admix = #{pkDeptAdmix,jdbcType=VARCHAR}
AND pivas.pk_dept_apply = #{pkDeptApply,jdbcType=VARCHAR}
AND pivas.code_batch = #{codeBatch,jdbcType=VARCHAR}
AND pivas.pk_pivascate = #{pkPivascate,jdbcType=VARCHAR}
AND pivas.eu_always = #{euAlways,jdbcType=VARCHAR}
AND to_char(pivas.time_occ,'YYYYMMDD') = #{curDate,jdbcType=VARCHAR}
AND to_char(pivas.time_occ,'YYYYMMDD') = #{nextDate,jdbcType=VARCHAR}
AND pv.code_pv = #{codePv,jdbcType=VARCHAR}
AND pi.name_pi LIKE '%${namePi}%'
AND pivas.eu_status = '0'
AND pivas.eu_status != '0'

select pv.bed_no, pi.name_pi, sex.name sex, pv.code_pv, dept.name_dept, pivas.ordsn, pivas.ordsn_parent, pivas.name_ord, pivas.spec, unit.name unit, freq.name freq, pivas.dosage, unit_dos.name unit_dos, pivas.time_occ, pivas.barcode, pivas.code_batch, pivas.pk_pdpivas, pivas.eu_status, pivas.pk_unit_dos from pi_master pi inner join pv_encounter pv on pi.pk_pi=pv.pk_pi inner join ex_pd_pivas pivas on pivas.pk_pv=pv.pk_pv left join bd_defdoc sex on pi.dt_sex=sex.code and sex.code_defdoclist='000000' left join bd_unit unit on pivas.pk_unit=unit.pk_unit left join bd_unit unit_dos on pivas.pk_unit_dos=unit_dos.pk_unit left join bd_term_freq freq on pivas.code_freq=freq.code left join bd_ou_dept dept on pv.pk_dept=dept.pk_dept where pivas.del_flag='0'
and pivas.pk_dept_admix=#{pkDeptAdmix,jdbcType=CHAR}
and pivas.flag_stop=0
and pivas.pk_dept_apply=#{pkDeptApply,jdbcType=CHAR}
and pivas.code_batch=#{codeBatch,jdbcType=VARCHAR}
and pivas.time_occ>=#{timeoccbegin,jdbcType=DATE}
and pivas.time_occ<#{timeoccend,jdbcType=DATE}
and pivas.eu_status>3 and pivas.eu_status<9
and pivas.eu_status=#{euStatus,jdbcType=VARCHAR}
and pv.code_pv=#{codePv,jdbcType=VARCHAR}
and pv.bed_no=#{bedNo,jdbcType=VARCHAR}
and pi.name_pi like '%'||#{namePi,jdbcType=VARCHAR}||'%'

select pv.bed_no, pi.name_pi, sex.name sex, pv.code_pv, dept.name_dept, pivas.ordsn, pivas.ordsn_parent, pivas.name_ord, pivas.spec, freq.name freq, sup.name sup, pivas.time_occ, pivas.date_sign, pivas.pk_emp_sign, pivas.name_emp_sign, de.name_dept emp_dept, pivas.barcode, pivas.code_batch, pivas.pk_pdpivas, pivas.pk_dept_admix, pivas.date_chk, pivas.dosage, pivas.flag_sign, pivas.pk_unit_dos from pi_master pi inner join pv_encounter pv on pi.pk_pi=pv.pk_pi inner join ex_pd_pivas pivas on pivas.pk_pv=pv.pk_pv left join bd_defdoc sex on pi.dt_sex=sex.code and sex.code_defdoclist='000000' left join bd_ou_dept de on de.pk_dept=pivas.pk_dept_apply left join bd_supply sup on pivas.code_supply=sup.code left join bd_term_freq freq on pivas.code_freq=freq.code left join bd_ou_dept dept on pv.pk_dept=dept.pk_dept where pivas.del_flag='0'
and pivas.pk_dept_apply=#{pkDeptApply,jdbcType=CHAR}
and pivas.flag_stop=0 and pivas.flag_chk=1
and pivas.pk_dept_admix=#{pkDeptAdmix,jdbcType=CHAR}
and pivas.code_batch=#{codeBatch,jdbcType=VARCHAR}
and pivas.time_occ>=#{timeoccbegin,jdbcType=DATE}
and pivas.time_occ<#{timeoccend,jdbcType=DATE}
and pivas.date_sign>=#{datesignbegin,jdbcType=DATE}
and pivas.date_sign<#{datesignend,jdbcType=DATE}
and pivas.flag_sign=#{flagSign,jdbcType=CHAR}
and pv.code_pv=#{codePv,jdbcType=VARCHAR}
and pv.bed_no=#{bedNo,jdbcType=VARCHAR}
and pi.name_pi like '%'||#{namePi,jdbcType=VARCHAR}||'%'
