mapper.select:
	select case sum(dt.amount) when null then 0 else sum(dt.amount) end amount,
		dt.pk_insurance
	from bl_settle st
	inner join bl_settle_detail dt on st.pk_settle=dt.pk_settle and dt.del_flag = '0'
	where st.del_flag = '0' and st.eu_pvtype in (1,2,4) and (st.flag_cc = '0' or st.pk_cc is null)
	      and st.pk_org = #{pkOrg,jdbcType=VARCHAR} 
	      and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR} 
	      and to_char(st.date_st,'yyyy-MM-DD HH24:MI:SS') <= #{dateEndStr,jdbcType=VARCHAR}
	group by dt.pk_insurance
	UNION
	SELECT
       sum(depo.amount) as amount,
       '1' as pk_insurance
      FROM bl_deposit_pi depo
      WHERE
        depo.del_flag = '0' AND (depo.flag_cc = 0 OR depo.pk_cc IS NULL)
          AND depo.pk_org = #{pkOrg,jdbcType=VARCHAR} 
        AND depo.pk_emp_pay = #{pkEmp,jdbcType=VARCHAR} 
         AND to_char(depo.date_pay, 'yyyy-MM-DD HH24:MI:SS') <= #{dateEndStr,jdbcType=VARCHAR}
    UNION
	    SELECT
	      sum(st.AMOUNT_ROUND) AS amount,
	      '2'              AS pk_insurance
		FROM bl_settle st
		where st.del_flag = '0' and st.eu_pvtype in (1,2,4) and (st.flag_cc = '0' or st.pk_cc is null)
	      and st.pk_org = #{pkOrg,jdbcType=VARCHAR} 
	      and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR} 
	      and to_char(st.date_st,'yyyy-MM-DD HH24:MI:SS') <= #{dateEndStr,jdbcType=VARCHAR}

select case when pv.pk_picate is null then pi.pk_picate else pv.pk_picate end as pk_picate, pv.pk_pv,pv.code_pv,pv.name_pi,pv.date_reg,
case when pv.PK_EMP_CANCEL = pv.PK_EMP_REG then pv.FLAG_CANCEL else case when pv.PK_EMP_REG= #{pkEmp,jdbcType=VARCHAR} then '0' when pv.PK_EMP_cancel= #{pkEmp,jdbcType=VARCHAR} then '1' else '0' end end flag_canc, pv.pk_insu,op.pk_dept_pv,op.pk_res,st.amount_st as Amt, doc2.name as PAYMODE from pv_encounter pv inner join pv_op op on pv.pk_pv=op.pk_pv and op.del_flag = '0' inner join pi_master pi on pv.pk_pi=pi.pk_pi and pi.del_flag = '0' inner join bl_settle st on st.pk_pv = pv.pk_pv and st.del_flag = '0' and st.eu_pvtype in (1,2,4) inner join bl_op_dt cg on cg.pk_settle=st.pk_settle and cg.flag_pv='1' and cg.del_flag = '0' inner join bl_cc cc on st.pk_cc=cc.pk_cc and cc.del_flag = '0' inner join bl_deposit depo on st.pk_settle=depo.pk_settle left join bd_defdoc doc2 on doc2.code = depo.DT_PAYMODE and doc2.CODE_DEFDOCLIST = '110100' and doc2.DEL_FLAG = '0'
where cc.del_flag = '0'
and pv.code_pv = #{codePv,jdbcType=VARCHAR}
and pv.name_pi like '%' || #{namePi,jdbcType=VARCHAR} || '%'
and cc.pk_cc = #{pkCc,jdbcType=VARCHAR}

SELECT pv.pk_pv, pv.code_pv, pv.name_pi, pv.date_reg, case when pv.PK_EMP_CANCEL = pv.PK_EMP_REG then pv.FLAG_CANCEL else case when pv.PK_EMP_REG= #{pkEmp,jdbcType=VARCHAR} then '0' when pv.PK_EMP_cancel= #{pkEmp,jdbcType=VARCHAR} then '1' else '0' end end flag_canc, pv.pk_insu, pi.pk_picate, op.pk_dept_pv, op.pk_res, st.amount_st as Amt, doc2.name as PAYMODE FROM pv_encounter pv INNER JOIN pv_op op ON pv.pk_pv = op.pk_pv AND op.del_flag = '0' INNER JOIN pi_master pi ON pv.pk_pi = pi.pk_pi AND pi.del_flag = '0' INNER JOIN bl_settle st ON st.pk_pv = pv.pk_pv AND st.del_flag = '0' AND st.eu_pvtype in (1,2,4) and (st.DT_STTYPE = '00' or st.DT_STTYPE = '20') inner join bl_deposit depo on st.pk_settle=depo.pk_settle left join bd_defdoc doc2 on doc2.code = depo.DT_PAYMODE and doc2.CODE_DEFDOCLIST = '110100' and doc2.DEL_FLAG = '0' WHERE pv.del_flag = '0' AND (st.flag_cc = 0 OR st.flag_cc IS NULL) AND pv.pk_org = #{pkOrg,jdbcType=VARCHAR} AND (pv.PK_EMP_REG = #{pkEmp,jdbcType=VARCHAR} or pv.PK_EMP_CANCEL = #{pkEmp,jdbcType=VARCHAR}) and st.PK_EMP_ST = #{pkEmp,jdbcType=VARCHAR}
AND to_char(st.date_st, 'yyyy-MM-DD HH24:MI:SS') <= #{dateEnd,jdbcType=VARCHAR}
and pv.code_pv = #{codePv,jdbcType=VARCHAR}
and pv.name_pi like '%' || #{namePi,jdbcType=VARCHAR} || '%'

select pi.code_pi, pi.name_pi, st.eu_stresult, st.date_st, CASE WHEN inv.CODE_INV is null THEN inv.EBILLNO ELSE inv.CODE_INV END code_inv, st.amount_st, sum(depo.amount) amt_pay, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, back.pk_settle from bl_settle st inner join bl_deposit depo on st.pk_settle=depo.pk_settle inner join pi_master pi on st.pk_pi=pi.pk_pi left outer join bl_st_inv si on st.pk_settle=si.pk_settle left outer join bl_invoice inv on si.pk_invoice=inv.pk_invoice left outer join bl_settle back on st.pk_settle=back.pk_settle_canc where st.del_flag = '0' and st.eu_pvtype in (1,2,4) and (st.flag_cc = 0 or st.flag_cc is null) and st.pk_org = #{pkOrg,jdbcType=CHAR} and st.pk_emp_st = #{pkEmp,jdbcType=CHAR}
and st.date_st <= to_date(#{dateEnd},'YYYYMMDDHH24MISS')
and inv.code_inv = #{codeInv,jdbcType=VARCHAR}
and pi.name_pi like '%${namePi}%'
group by pi.code_pi, pi.name_pi, st.eu_stresult, st.date_st, inv.code_inv, st.amount_st, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, back.pk_settle order by st.date_st

select pi.code_pi, pi.name_pi, st.amount_st, de.amount as amount_pi, st.date_st, st.eu_stresult, CASE WHEN inv.CODE_INV is null THEN inv.EBILLNO ELSE inv.CODE_INV END CODE_INV, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, doc.NAME as dt_stname, doc2.name as DT_PAYMODE, st.pk_settle from bl_settle st inner join pi_master pi on st.pk_pi=pi.pk_pi and pi.del_flag = '0' join BL_DEPOSIT de on de. pk_settle =st.PK_SETTLE left outer join bl_st_inv si on st.pk_settle=si.pk_settle and si.del_flag = '0' left outer join bl_invoice inv on si.pk_invoice=inv.pk_invoice and inv.del_flag = '0' left join bd_defdoc doc on doc.code = st.dt_sttype and doc.CODE_DEFDOCLIST = '110102' and doc.DEL_FLAG = '0' left join bd_defdoc doc2 on doc2.code = de.DT_PAYMODE and doc2.CODE_DEFDOCLIST = '110100' and doc2.DEL_FLAG = '0' where st.del_flag = '0' and st.eu_pvtype in (1,2,4) and (st.flag_cc = 0 or st.flag_cc is null)
and st.pk_org = #{pkOrg,jdbcType=CHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=CHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=CHAR},'YYYYMMDDHH24MISS')
and inv.code_inv = #{codeInv,jdbcType=CHAR}
and (pi.name_pi like '%${namePi}%' or pi.name_pi like '%${namePi}%')
group by pi.code_pi, pi.name_pi, st.pk_settle, st.code_st, st.date_st, st.eu_stresult, st.amount_st, st.amount_pi, inv.code_inv, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, doc.NAME, doc2.name,inv.EBILLNO,de.amount order by st.code_st asc

select pi.code_pi, pi.name_pi, st.amount_st, de.amount as amount_pi, st.date_st, st.eu_stresult, CASE WHEN inv.CODE_INV is null THEN inv.EBILLNO ELSE inv.CODE_INV END CODE_INV, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, doc.NAME as dt_stname,doc2.name as DT_PAYMODE, st.pk_settle from bl_settle st inner join pi_master pi on st.pk_pi=pi.pk_pi and pi.del_flag = '0' join BL_DEPOSIT de on de. pk_settle =st.PK_SETTLE left outer join bl_st_inv si on st.pk_settle=si.pk_settle and si.del_flag = '0' left outer join bl_invoice inv on si.pk_invoice=inv.pk_invoice and inv.del_flag = '0' left join bd_defdoc doc on doc.code = st.dt_sttype and doc.CODE_DEFDOCLIST = '110102' and doc.DEL_FLAG = '0' left join bd_defdoc doc2 on doc2.code = de.DT_PAYMODE and doc2.CODE_DEFDOCLIST = '110100' and doc2.DEL_FLAG = '0' where st.del_flag = '0'
and st.pk_cc = #{pkCc,jdbcType=CHAR}
and inv.code_inv = #{codeInv,jdbcType=CHAR}
and (pi.name_pi like '%${namePi}%' or pi.name_pi like '%${namePi}%')
GROUP BY pi.code_pi, pi.name_pi, st.pk_settle, st.code_st, st.date_st, st.eu_stresult, st.amount_st, st.amount_pi, inv.code_inv, st.flag_canc, st.pk_settle_canc, st.reason_canc, inv.flag_cancel, inv.note, doc.NAME,doc2.name ,inv.EBILLNO,de.amount order by st.code_st asc

SELECT pv.pk_pv, pv.code_pv, pv.name_pi, pv.date_reg, pv.pk_insu, pi.pk_picate, op.pk_dept_pv, op.pk_res, st.amount_st as Amt, case when pv.PK_EMP_CANCEL = pv.PK_EMP_REG then pv.FLAG_CANCEL else case when pv.PK_EMP_REG= '${pkEmp}' then '0' when pv.PK_EMP_cancel= '${pkEmp}' then '1' else '0' end end flag_canc, to_char(wm_concat(pay.name)) AS paymode FROM pv_encounter pv INNER JOIN pv_op op ON pv.pk_pv = op.pk_pv AND op.del_flag = '0' INNER JOIN pi_master pi ON pv.pk_pi = pi.pk_pi AND pi.del_flag = '0' INNER JOIN bl_settle st ON st.pk_pv = pv.pk_pv AND st.del_flag = '0' AND st.eu_pvtype in (1,2,4) and (st.DT_STTYPE = '00' or st.DT_STTYPE = '20') LEFT JOIN (SELECT depo.PK_SETTLE, doc.NAME FROM BL_DEPOSIT depo LEFT JOIN BD_DEFDOC doc ON doc.code = depo.dt_paymode AND doc.DEL_FLAG = '0' AND doc.CODE_DEFDOCLIST = '110100' WHERE depo.EU_PVTYPE in (1,2,4) GROUP BY depo.PK_SETTLE, doc.NAME) pay ON pay.pk_settle = st.pk_settle WHERE pv.del_flag = '0' AND (st.flag_cc = 0 OR st.flag_cc IS NULL) AND pv.pk_org = #{pkOrg,jdbcType=VARCHAR} AND (pv.PK_EMP_REG = #{pkEmp,jdbcType=VARCHAR} or pv.PK_EMP_CANCEL = #{pkEmp,jdbcType=VARCHAR}) and st.PK_EMP_ST = #{pkEmp,jdbcType=VARCHAR}
AND to_char(st.date_st, 'yyyy-MM-DD HH24:MI:SS') <= #{dateEnd,jdbcType=VARCHAR}
and pv.code_pv = #{codePv,jdbcType=VARCHAR}
and pv.name_pi like '%' || #{namePi,jdbcType=VARCHAR} || '%'
group by pv.pk_pv, pv.code_pv, pv.name_pi, pv.date_reg, pv.pk_insu, pi.pk_picate, op.pk_dept_pv, op.pk_res, case when pv.PK_EMP_CANCEL = pv.PK_EMP_REG then pv.FLAG_CANCEL else case when pv.PK_EMP_REG= '${pkEmp}' then '0' when pv.PK_EMP_cancel= '${pkEmp}' then '1' else '0' end end, st.amount_st

select st.pk_settle from bl_settle st where st.eu_pvtype in (1,2,4) and st.flag_cc='0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and (st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR} or st.pk_emp_st =#{pkEmp,jdbcType=VARCHAR})
and to_char(st.date_st,'yyyy-MM-DD HH24:MI:SS') <= #{dateEndStr,jdbcType=VARCHAR}
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')

select ybst.code,sum(ybst.tczf) tczf,sum(ybst.grzf) grzf from ( select '07' code, case when sum(city.amt_jjzf) is null then 0 else sum(city.amt_jjzf) end tczf, case when sum(city.amt_grzhzf) is null then 0 else sum(city.amt_grzhzf) end grzf from ins_szyb_st_city city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join bl_settle st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.code = '07' and st.eu_pvtype in (1,2,4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')
union all select '05' code, case when sum(city.akb068) is null then 0 else sum(city.akb068) end tczf, case when sum(city.akb066) is null then 0 else sum(city.akb066) end grzf from ins_szyb_st_diff city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join bl_settle st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance left join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where (fahp.code = '05' or hp.code = '05') and st.eu_pvtype in (1,2,4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')
union all select '06' code, case sum(city.akb068) when null then 0 else sum(city.akb068) end tczf, case sum(city.akb066) when null then 0 else sum(city.akb066) end grzf from ins_szyb_st_diff city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join bl_settle st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance left join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where (fahp.code = 'Z06' or hp.code = 'Z06') and st.eu_pvtype in (1,2,4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')
union all select '07' code, case when sum(city.amt_jjzf) is null then 0 else sum(city.amt_jjzf) end *-1 tczf, case when sum(city.amt_grzhzf) is null then 0 else sum(city.amt_grzhzf) end *-1 grzf from ins_szyb_st_city city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join ( select bl_st.pk_settle,bl_st.pk_insurance,st_cal.pk_cc,bl_st.pk_pv,st_cal.date_st,st_cal.pk_emp_st,st_cal.flag_cc,st_cal.pk_org from bl_settle bl_st inner join bl_settle st_cal on st_cal.pk_settle_canc = bl_st.pk_settle where bl_st.eu_pvtype in (1,2,4)
and (bl_st.dt_sttype='00' or bl_st.DT_STTYPE = '20')
and (bl_st.dt_sttype='01' or bl_st.DT_STTYPE = '21')
) st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.code = '07'
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
union all select '05' code, case when sum(city.akb068) is null then 0 else sum(city.akb068) end *-1 tczf, case when sum(city.akb066) is null then 0 else sum(city.akb066) end *-1 grzf from ins_szyb_st_diff city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join ( select bl_st.pk_settle,bl_st.pk_insurance,st_cal.pk_cc,bl_st.pk_pv,st_cal.date_st,st_cal.pk_emp_st,st_cal.flag_cc,st_cal.pk_org from bl_settle bl_st inner join bl_settle st_cal on st_cal.pk_settle_canc = bl_st.pk_settle where bl_st.eu_pvtype in (1,2,4)
and (bl_st.dt_sttype='00' or bl_st.DT_STTYPE = '20')
and (bl_st.dt_sttype='01' or bl_st.DT_STTYPE = '21')
) st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance left join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where (fahp.code = '05' or hp.code = '05')
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
union all select '06' code, case sum(city.akb068) when null then 0 else sum(city.akb068) end *-1 tczf, case sum(city.akb066) when null then 0 else sum(city.akb066) end *-1 grzf from ins_szyb_st_diff city inner join ins_szyb_st ybst on ybst.pk_insst = city.pk_insst inner join ( select bl_st.pk_settle,bl_st.pk_insurance,st_cal.pk_cc,bl_st.pk_pv,st_cal.date_st,st_cal.pk_emp_st,st_cal.flag_cc,st_cal.pk_org from bl_settle bl_st inner join bl_settle st_cal on st_cal.pk_settle_canc = bl_st.pk_settle where bl_st.eu_pvtype in (1,2,4)
and (bl_st.dt_sttype='00' or bl_st.DT_STTYPE = '20')
and (bl_st.dt_sttype='01' or bl_st.DT_STTYPE = '21')
) st on st.pk_settle = ybst.pk_settle inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance left join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where (fahp.code = 'Z06' or hp.code = 'Z06')
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
) ybst group by ybst.code

select max(tmp.code_inv) endcode, min(tmp.code_inv) begincode, count(1) cnt from ( SELECT inv.pk_invcate, inv.EBILLNO code_inv, inv.EBILLNO cc,
row_number() over(order by inv.EBILLNO asc) as rownum FROM bl_st_inv stinv INNER JOIN bl_invoice inv ON stinv.pk_invoice = inv.pk_invoice INNER JOIN bl_settle st ON st.pk_settle = stinv.pk_settle WHERE inv.EBILLNO is not null
and inv.pk_cc_ebill = #{pkCc,jdbcType=VARCHAR}
AND inv.flag_cc_ebill = 0
and inv.pk_org = #{pkOrg,jdbcType=VARCHAR}
and inv.pk_emp_ebill = #{pkEmp,jdbcType=VARCHAR}
and inv.date_ebill <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype = '00' or st.dt_sttype = '20' )
and (st.dt_sttype = '01' or st.dt_sttype = '21' )
) tmp
group by convert(INT,(tmp.cc-tmp.rownum)) order by begincode asc

select inv.pk_invcate, inv.pk_empinvoice, inv.EBILLNO code_inv from bl_invoice inv left join bd_invcate cate on inv.pk_invcate=cate.pk_invcate and cate.eu_type = '0' inner join bl_st_inv stinv on stinv.pk_invoice = inv.pk_invoice INNER JOIN bl_settle st ON st.pk_settle = stinv.pk_settle where inv.flag_cancel_ebill = '1' and inv.EBILLNO is not null
and inv.pk_cc_cancel_ebill = #{pkCc,jdbcType=VARCHAR}
and inv.flag_cc_cancel_ebill = '0'
and inv.pk_org = #{pkOrg,jdbcType=VARCHAR}
and inv.pk_emp_cancel_ebill = #{pkEmp,jdbcType=VARCHAR}
and inv.date_ebill_cancel <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype = '00' or st.dt_sttype = '20' )
and (st.dt_sttype = '01' or st.dt_sttype = '21' )
order by inv.code_inv asc

select ybst.code, sum(ybst.amt) amt,sum(ybst.amt_back) amt_back from ( select '01' code, case when sum(ybst.amt_jjzf) is null then 0 else sum(ybst.amt_jjzf) end amt, 0 as amt_back from ins_qgyb_st ybst inner join bl_settle st on st.pk_settle = ybst.pk_settle and st.DT_STTYPE = '01' inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.DT_EXTHP = '01' and st.eu_pvtype in (1,2,4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')
union all select '01' code, 0 as amt, case when sum(ybcancel.amt_jjzf) is null then 0 else sum(ybcancel.amt_jjzf) end amt_back from ins_qgyb_st ybcancel left join ins_qgyb_st ybst on ybst.PK_INSST = ybcancel.PK_INSST_CANCEL inner join bl_settle st on st.pk_settle = ybst.pk_settle and st.DT_STTYPE = '01' inner join bl_settle stcanc on stcanc.PK_SETTLE_CANC = st.pk_settle and stcanc.DT_STTYPE = '21' inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.DT_EXTHP = '01' and st.eu_pvtype in (1,2,4)
and stcanc.pk_cc = #{pkCc,jdbcType=VARCHAR}
and stcanc.flag_cc = '0'
and stcanc.pk_org = #{pkOrg,jdbcType=VARCHAR}
and stcanc.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and stcanc.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (stcanc.dt_sttype='00' or stcanc.DT_STTYPE = '20')
and (stcanc.dt_sttype='01' or stcanc.DT_STTYPE = '21')
union all select '02' code, case when sum(ybst.amt_jjzf) is null then 0 else sum(ybst.amt_jjzf) end amt, 0 as amt_back from ins_sgsyb_st ybst inner join bl_settle st on st.pk_settle = ybst.pk_settle and st.DT_STTYPE = '01' inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.DT_EXTHP = '02' and st.eu_pvtype in (1,2,4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')
union all select '02' code, 0 as amt, case when sum(sgscancel.amt_jjzf) is null then 0 else sum(sgscancel.amt_jjzf) end amt_back from ins_sgsyb_st sgscancel left join ins_sgsyb_st sgs on sgs.PK_INSST = sgscancel.PK_INSST_CANCEL inner join BL_SETTLE st on st.PK_SETTLE_CANC = sgs.PK_SETTLE and st.DT_STTYPE = '01' inner join bl_settle stcanc on stcanc.PK_SETTLE_CANC = st.pk_settle and stcanc.DT_STTYPE = '21' inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv inner join bd_hp hp on hp.pk_hp = st.pk_insurance inner join bd_hp fahp on fahp.pk_hp = hp.PK_PARENT where fahp.DT_EXTHP = '02' and st.eu_pvtype in (1,2,4)
and stcanc.pk_cc = #{pkCc,jdbcType=VARCHAR}
and stcanc.flag_cc = '0'
and stcanc.pk_org = #{pkOrg,jdbcType=VARCHAR}
and stcanc.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and stcanc.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (stcanc.dt_sttype='00' or stcanc.DT_STTYPE = '20')
and (stcanc.dt_sttype='01' or stcanc.DT_STTYPE = '21')
) ybst group by ybst.code

select st.PK_SETTLE
from bl_settle st inner join PV_ENCOUNTER pv on pv.pk_pv = st.pk_pv
where st.eu_pvtype in (1, 2, 4)
and st.pk_cc = #{pkCc,jdbcType=VARCHAR}
and st.flag_cc = '0'
and st.pk_org = #{pkOrg,jdbcType=VARCHAR}
and st.pk_emp_st = #{pkEmp,jdbcType=VARCHAR}
and st.date_st <= to_date(#{dateEnd,jdbcType=VARCHAR}, 'yyyymmddhh24miss')
and (st.dt_sttype='00' or st.DT_STTYPE = '20')
and (st.dt_sttype='01' or st.DT_STTYPE = '21')

SELECT isnull(sum( a.AMOUNT_ST ) ,0) 
		FROM bl_settle a
	where a.PK_CC  = #{pkCc,jdbcType=VARCHAR}
